import React, { useEffect, useState } from "react";
import axios from "axios";
import CoursForm from "./components/CoursForm";
import CoursList from "./components/CoursList";

const API_URL = "http://127.0.0.1/lms_react/api/cours/";

function App() {
  const [cours, setCours] = useState([]);
  const [titre, setTitre] = useState("");
  const [description, setDescription] = useState("");
  const [editId, setEditId] = useState(null);

  const fetchCours = async () => {
    try {
      const res = await axios.get(API_URL + "get.php");
      console.log("Re√ßu :", res.data);
      setCours(res.data);
    } catch (err) {
      console.error("Erreur axios :", err);
    }
  };

  const createOrUpdateCours = async () => {
    if (editId) {
      await axios.put(API_URL + "update.php", { id: editId, titre, description });
    } else {
      await axios.post(API_URL + "create.php", { titre, description });
    }
    setTitre("");
    setDescription("");
    setEditId(null);
    fetchCours();
  };

  const editCours = (cours) => {
    setTitre(cours.titre);
    setDescription(cours.description);
    setEditId(cours.id);
  };

  const deleteCours = async (id) => {
    await axios.delete(API_URL + "delete.php", { data: { id } });
    fetchCours();
  };

  useEffect(() => {
    fetchCours();
  }, []);

  return (
    // <div style={{ padding: 20 }}>
    //   <h1>Gestion des cours üìö</h1>
    //   <CoursForm onSubmit={createOrUpdateCours} cours={editCours} />
    //   <CoursList cours={cours} onEdit={editCours} onDelete={deleteCours} />
    // </div>
    <div style={{ padding: 40, maxWidth: 600, margin: "auto" }}>
      <h1>üìö Liste des cours</h1>

      <div style={{ marginBottom: 30 }}>
        <h2>{editId ? "Modifier un cours" : "Ajouter un nouveau cours"}</h2>
        <input
          type="text"
          placeholder="Titre"
          value={titre}
          onChange={(e) => setTitre(e.target.value)}
          style={{
            display: "block",
            width: "100%",
            padding: 8,
            marginBottom: 10,
          }}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          style={{
            display: "block",
            width: "100%",
            padding: 8,
            height: 100,
            marginBottom: 10,
          }}
        />
        <button onClick={createOrUpdateCours} style={{ padding: "10px 20px" }}>
          {editId ? "Modifier" : "Ajouter"}
        </button>
      </div>

      <ul>
        {cours.length === 0 && <li>Aucun cours pour le moment.</li>}
        {cours.map((c) => (
          <li key={c.id} style={{ marginBottom: 10 }}>
            <strong>{c.titre}</strong> ‚Äî {c.description}
            <br />
            <button onClick={() => editCours(c)} style={{ marginRight: 10 }}>
              ‚úèÔ∏è Modifier
            </button>
            <button onClick={() => deleteCours(c.id)}>üóëÔ∏è Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
