import CategoriesItem from "./CategoriesItem";
import React, { useEffect, useState } from 'react';

function CategoriesList({ categories, onEdit, onDelete }) {
  useEffect(() => {
    // Exemple de récupération simulée d'API
    fetch('/api/categories')
      .then(res => res.json())
      .then(data => {
        console.log("API returned:", data);
        setCategories(data);
      })
      .catch(err => console.error(err));
  }, []);

  return (
    <ul>
      {categories.length === 0 && <li>Aucune catégorie pour le moment.</li>}
      {categories.map((c) => (
        <CategoriesItem key={c.id} categories={c} onEdit={onEdit} onDelete={onDelete} />
      ))}
    </ul>
  );
}

export default CategoriesList;
