import CategoriesItem from "./CategoriesItem";
import React, { useEffect, useState } from 'react';

function CategoriesList({ categories, onEdit, onDelete }) {
  useEffect(() => {
    fetch('/api/categories')
      .then(res => res.json())
      .then(data => {
        console.log("Données reçues :", data);
        // assure-toi que data est un tableau
        if (Array.isArray(data)) {
          setCategories(data);
        } else {
          console.warn("Les données ne sont pas un tableau :", data);
          setCategories([]); // fallback
        }
      });
  }, []);

  return (
    <ul>
      {categories.map((c) => (
        <CategoriesItem key={c.id} categories={c} onEdit={onEdit} onDelete={onDelete} />
      ))}
    </ul>
  );
}

export default CategoriesList;
