import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = "http://127.0.0.1/lms_react/api/cours/";

function App() {
  const [cours, setCours] = useState([]);
  const [titre, setTitre] = useState("");
  const [description, setDescription] = useState("");
  const [editId, setEditId] = useState(null);

  const fetchCours = async () => {
    try {
      const res = await axios.get(API_URL + "get.php");
      console.log("Re√ßu :", res.data);
      setCours(res.data);
    } catch (err) {
      console.error("Erreur axios :", err);
    }
  };

  const createOrUpdateCours = async () => {
    if (editId) {
      await axios.put(API_URL + "update.php", { id: editId, titre, description });
    } else {
      await axios.post(API_URL + "create.php", { titre, description });
    }
    setTitre("");
    setDescription("");
    setEditId(null);
    fetchCours();
  };

  const editCours = (cours) => {
    setTitre(cours.titre);
    setDescription(cours.description);
    setEditId(cours.id);
  };

  const deleteCours = async (id) => {
    await axios.delete(API_URL + "delete.php", { data: { id } });
    fetchCours();
  };

  useEffect(() => {
    fetchCours();
  }, []);

  return (
    <div style={{ padding: 20, maxWidth: 600, margin: "auto" }}>
      <h1>Liste des cours</h1>
      <div style={{ marginBottom: 20 }}>
        <input placeholder="Titre" value={titre} onChange={(e) => setTitre(e.target.value)} />
      </div>
      <br />
      <textarea placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} />
      <br />
      <button onClick={createOrUpdateCours}>{editId ? "Modifier" : "Ajouter"} le cours</button>

      <ul>
        {cours.length === 0 && <li>Aucun cours pour le moment.</li>}
        {cours.map((c) => (
          <li key={c.id}>
            <strong>{c.titre}</strong> ‚Äî {c.description}
            <button onClick={() => editCours(c)}>‚úèÔ∏è</button>
            <button onClick={() => deleteCours(c.id)}>üóëÔ∏è</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
