import React from "react";
import CategoriesItem from "./CategoriesItem";

function CategoriesList({ categories, onEdit, onDelete }) {
  return (
    <ul>
      {categories.id === 0 && <li>Aucune cat√©gorie pour le moment.</li>}
      {categories.map((c) => (
        <CategoriesItem key={categories.id} categories={categories.nom} onEdit={onEdit} onDelete={onDelete} />
      ))}
    </ul>
  );
}

export default CategoriesList;
